query BlogPostQuery(
  $slug: String!
) {
  markdownRemark(frontmatter: {slug: {eq: $slug}}) {
    html
    frontmatter {
      date
      slug
      title
    }
  }
}

query BlogIndexQuery {
  allMarkdownRemark(sort: {fields: frontmatter___cijena, order: DESC}) {
    edges {
      node {
        id
        frontmatter {
          cijena
          title
          date
          slug
          slika {
            publicURL
          }
        }
        excerpt(pruneLength: 300)
      }
    }
  }
}
